import React from 'react'

export const Paginator = ({pages, pressHandler, page}) => {
    let arrPages = []

    for (let i = 1; i <= pages; i++) {
        arrPages.push(i);
    }

    return (
        <div className='paginator-wrapper'>
            {arrPages.map((el, index) => (
                <p key={index} 
                onClick={() => pressHandler(el)}
                className={+page === +(index + 1) ? 'paginator-wrapper__item active' : 'paginator-wrapper__item'}>
                    {index + 1}
                </p>
            ))}
        </div>
    )
}
